import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BoardIcon from '../constans/icons/boardIcon';
import CircleIcon from '../constans/icons/circleIcon';
import '../css/Game.css';

function Game() {
  const [questionList, setQuestionList] = useState([]);
  const [answer, setAnswer] = useState({});


  let navigate = useNavigate();
  const routeChange = () =>{ 
    navigate(`/finish`);
  };

  const randomNumber = () => {
    return Math.floor(Math.random() * (11 - 1)) + 1;
  };

    let firstNumber = randomNumber();
    let secondNumber = randomNumber();

    let siklar = [];

    function shuffle(a) {
      for (let i = a.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [a[i], a[j]] = [a[j], a[i]];
      }
      return a;
  }

  const game = () => {
    let choiceTrue = firstNumber * secondNumber;
    let choice1 = (firstNumber-1) * (secondNumber);
    let choice2 = (firstNumber) * (secondNumber+1);

    siklar.push(choice1,choice2, choiceTrue);
    console.log(siklar);


    shuffle(siklar);
    console.log(siklar);
    setQuestionList(siklar)
    console.log("firstAN", questionList)


    console.log("choiceTrue", choiceTrue)
    console.log("choice1", choice1)
    console.log("choice2", choice2)

  }
  console.log("firstAN", questionList)


  useEffect(() => {
    game()
  },[]);


  return (
    <>
        <div>Game</div>
        <a href='/finish'>Finish</a>

        <div className="navbar">
        <div className="navbar_title">
            <div>Score: 120</div>
            <div>Tour: 2</div>
            <div>Questions: 6/7</div>
        </div>
    </div>

    <div className="container">

        <div className="container-board">
            <BoardIcon />
            <div className='container-board-question title'>
              {`${firstNumber} x ${secondNumber}`}
            </div>
        </div>

        <div className="container-selection">
            <div className="container-selection_1">
                <p className="container-selection-text">{`${siklar[0]}`}</p>
                <CircleIcon />
            </div>

            <div className="container-selection_2">
                <p className="container-selection-text">siklar[1]</p>
                <CircleIcon />
            </div>

            <div className="container-selection_3">
                <p className="container-selection-text">siklar[2]</p>
                <CircleIcon />
            </div>

        </div>

    </div> 
    </>
  );
}

export default Game;
