import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BoardIcon from '../constans/icons/boardIcon';
import CircleIcon from '../constans/icons/circleIcon';
import '../css/Game.css';

function Game() {
  const [questionList, setQuestionList] = useState({});
  const [answer, setAnswer] = useState({});

  let navigate = useNavigate();
  const routeChange = () =>{ 
    navigate(`finish`);
  };

  const randomNumber = () => {
    return Math.floor(Math.random() * (11 - 1)) + 1;
  };


  const game = () => {

    let firstNumber = randomNumber();
    let secondNumber = randomNumber();

    console.log("firstNumber", firstNumber)
    console.log("secondNumber", secondNumber)

    let choiceTrue = firstNumber * secondNumber;
    let choice1 = (firstNumber-1) * (secondNumber);
    let choice2 = (firstNumber) * (secondNumber+1);

    let siklar = [];

    siklar.push(choice1);
    siklar.push(choice2);
    siklar.push(choiceTrue);

    console.log("Şıklar", siklar);

    setQuestionList({firstNumber: firstNumber, secondNumber: secondNumber});

    console.log("cevap",choiceTrue)
    console.log("choie1", choice1)
    console.log("choie2", choice2)
  }


  useEffect(() => {

    // let firstNumber= randomNumber();
    // let secondNumber= randomNumber();

    // b.push({firstNumber: firstNumber, secondNumber: secondNumber, answer: true});
    // b.push({firstNumber: firstNumber, secondNumber: secondNumber, answer: true});
    game();

  },[]);

  console.log("AAAA",questionList);

  return (
    <>
        <div>Game</div>
        <a href='/finish'>Finish</a>

        <div className="navbar">
        <div className="navbar_title">
            <div>Score: 120</div>
            <div>Tour: 2</div>
            <div>Questions: 6/7</div>
        </div>
    </div>

    <div className="container">

        <div className="container-board">
            <BoardIcon />
            <div className='container-board-question title'>
              {`${firstNumber} x ${secondNumber}`}
            </div>
        </div>

        <div className="container-selection">

            <div className="container-selection_1">
                <p className="container-selection-text">15</p>
                <CircleIcon />
            </div>

            <div className="container-selection_2">
                <p className="container-selection-text">25</p>
                <CircleIcon />
            </div>

            <div className="container-selection_3">
                <p className="container-selection-text">35</p>
                <CircleIcon />
            </div>

        </div>

    </div> 
    </>
  );
}

export default Game;
