import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BoardIcon from '../constans/icons/boardIcon';
import CircleIcon from '../constans/icons/circleIcon';
import '../css/Game.css';

function Game() {
  const [questionList, setQuestionList] = useState([]);
  const [answer, setAnswer] = useState({});

  let firstNumber;
  let secondNumber;
  let key = [];

  let navigate = useNavigate();
  const routeChange = () =>{ 
    navigate(`/finish`);
  };

  const randomNumber = () => {
    let a = Math.floor(Math.random() * (11 - 1)) + 1;
    return a;
  };

  function shuffle(a) {
    for (let i = a.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [a[i], a[j]] = [a[j], a[i]];
    }
      return a;
  }

  function gameStart (){
    firstNumber = randomNumber();
    secondNumber = randomNumber();

   document.getElementById('container-board-question').innerHTML += 12;

    console.log('first', firstNumber)
    console.log('second', secondNumber)

    let choie1 = (firstNumber-1) * secondNumber;
    let choie2 = firstNumber * (secondNumber+1);
    let choieTrue = firstNumber * secondNumber;

    console.log('choie1', choie1)
    console.log('choie2', choie2)
    console.log('choieTrue', choieTrue)

    key.push(choie1,choie2,choieTrue);
    console.log(key)

    shuffle(key)
    console.log(key)
  }




  useEffect(() => {
    gameStart();
    console.log("first", key[0])
    // console.log('first', firstNumber)
    // console.log('second', secondNumber)
    // console.log('choie1', choie1)
    // console.log('choie2', choie2)
    // console.log('choieTrue', choieTrue)
  },[]);


  return (
    <>
        <div>Game</div>
        <a href='/finish'>Finish</a>

        <div className="navbar">
        <div className="navbar_title">
            <div>Score: 120</div>
            <div>Tour: 2</div>
            <div>Questions: 6/7</div>
        </div>
    </div>

    <div className="container">

        <div className="container-board">
            <BoardIcon />
            <div className='container-board-question title'>
              {`${firstNumber} x ${secondNumber}`}
            </div>
        </div>

        <div className="container-selection">
            <div className="container-selection_1">
                <p className="container-selection-text">{`${key[0]}`}</p>
                <CircleIcon />
            </div>

            <div className="container-selection_2">
                <p className="container-selection-text">{`${key[1]}`}</p>
                <CircleIcon />
            </div>

            <div className="container-selection_3">
                <p className="container-selection-text">{`${key[2]}`}</p>
                <CircleIcon />
            </div>

        </div>

    </div> 
    </>
  );
}

export default Game;
